ContactPhoneToProfileId.map:
function (doc, mets) {
    if (doc._class === 'DeviceContacts') {
        if (doc.contacts) {
            doc.contacts.forEach(function (c) {
                if (c.phone_number_normalized) {
                    c.phone_number_normalized.forEach(function (pnn) {
                        emit(pnn, doc.user_id);
                    });
                }
            });
        }
    }
}
