DeviceIdPhoneToContact.map:
function (doc, meta) {
    if (doc._class === 'DeviceContacts') {
        if (doc.contacts) {
            doc.contacts.forEach(function (contact) {
                if (contact.phone_number_normalized) {
                    contact.phone_number_normalized.forEach(function (pnn) {
                      	var contactDto = {"name":contact.name,"avatar_uri":contact.avatar_uri,"native_contact_id":contact.native_contact_id}
                        emit(doc.device_id+"::"+pnn, contactDto);
                    });
                }
            });
        }
    }
}
